export declare let Map: new ()=>any; 

if (!Map){
    Map = MapPolyfill;
};

export class MapPolyfill {
    store: any;

    constructor (){
    	this.store = {};        
    };

    find(keyObj){
        return JSON.stringify(keyObj);
    };

    has(keyObj){
        return this.find(keyObj) in this.store;
    };

    get(keyObj){	
        return this.store[this.find(keyObj)];
    };

    set(keyObj, value){
        this.store[this.find(keyObj)] = value;
    };
};


