"use strict";

export declare var Map: new ()=>any; 

export class MapPolyfill {
    store: any;

    constructor (){
    	this.store = {};        
    };

    find(keyObj: any){
        return JSON.stringify(keyObj);
    };

    has(keyObj: any){
        return this.find(keyObj) in this.store;
    };

    get(keyObj: any){	
        return this.store[this.find(keyObj)];
    };

    set(keyObj: any, value: any){
        this.store[this.find(keyObj)] = value;
    };
};

if (!Map){
    Map = MapPolyfill;
};
